import React, { useEffect } from 'react';
import BoyWithSunglasses from './Image/3d-render-boy-with-orange-tshirt-sunglasses.jpg';
import Roadmap from './Roadmap';
import './Home.css';

const Home = () => {
  useEffect(() => {
    const sections = document.querySelectorAll('.section');

    const handleScroll = () => {
      sections.forEach((section) => {
        const sectionTop = section.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;
        if (sectionTop < windowHeight * 0.75) {
          section.classList.add('animate');
        } else {
          section.classList.remove('animate');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="home-container">
      <section className="section about-me">
        <div className="content left">
          <h2>About Me</h2>
          <p>Your about me content goes here...</p>
        </div>
      </section>

      <section className="section roadmap">
        <Roadmap />
      </section>

      <section className="section animated-image">
        <div className="content center">
          <h2>3D Render Boy with Orange T-Shirt and Sunglasses</h2>
        </div>
      </section>

      <section className="section coding-work">
        <div className="content left">
          <a href="/coding-work">
            <div className="tile">
              <h2>Coding Work</h2>
              <p>Details about your coding projects go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
      </section>

      <section className="section photographs">
        <div className="content right">
          <a href="/photographs">
            <div className="tile">
              <h2>Photographs</h2>
              <p>Details about your photographs go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
      </section>
    </div>
  );
};

export default Home;
