// src/components/Roadmap.js
import React from 'react';
import { motion } from 'framer-motion';
import './css/Roadmaps.css';

const Roadmap = () => {
  const roadmapItems = [
    { title: "Started Learning", content: "Started my journey in the tech world by learning basic HTML, CSS, and JavaScript." },
    { title: "First Project", content: "Developed my first web application, a simple to-do list app." },
    { title: "Professional Experience", content: "Started working as a junior developer at a tech company, gaining valuable experience." },
    { title: "Current Position", content: "Currently working as a full-stack developer, focusing on modern web technologies and AI integration." },
  ];

  return (
    <div className="roadmap-container">
      {roadmapItems.map((item, index) => (
        <React.Fragment key={index}>
          <motion.div
            className={`roadmap-item ${index % 2 === 0 ? 'left' : 'right'}`}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.3 }}
          >
            <div className="roadmap-content">
              <h3>{item.title}</h3>
              <p>{item.content}</p>
            </div>
          </motion.div>
          {index < roadmapItems.length - 1 && (
            <motion.div
              className="arrow"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: index * 0.3 + 0.2 }}
            >
              <img src="/arrow-down.svg" alt="Arrow" />
            </motion.div>
          )}
        </React.Fragment>
      ))}
    </div>
  );
};

export default Roadmap;
