// src/components/Roadmap.js
import React from 'react';
import { motion } from 'framer-motion';
import './css/Roadmaps.css';

const roadmapVariants = {
  float: {
    y: [0, 20, 0], // Float effect
    transition: {
      y: {
        repeat: Infinity,
        repeatType: 'mirror',
        duration: 2,
        ease: 'easeInOut',
      },
    },
  },
};

const Roadmap = () => {
  return (
    <>
      {[
        { title: "Started Learning", content: "Started my journey in the tech world by learning basic HTML, CSS, and JavaScript." },
        { title: "First Project", content: "Developed my first web application, a simple to-do list app." },
        { title: "Professional Experience", content: "Started working as a junior developer at a tech company, gaining valuable experience." },
        { title: "Current Position", content: "Currently working as a full-stack developer, focusing on modern web technologies and AI integration." },
      ].map((item, index) => (
        <motion.div
          className="roadmap-item"
          key={index}
          variants={roadmapVariants}
          initial="float"
          animate="float"
          drag
          dragConstraints={{ top: -200, bottom: 200, left: -200, right: 200 }}
          whileTap={{ scale: 0.9 }}
          style={{
            position: 'absolute', // Removed from the container, absolute positioning
            top: `${Math.random() * 80 + 10}vh`, // Randomly place items within the viewport height
            left: `${Math.random() * 80 + 10}vw`, // Randomly place items within the viewport width
          }}
        >
          <div className="roadmap-content">
            <h3>{item.title}</h3>
            <p>{item.content}</p>
          </div>
        </motion.div>
      ))}
    </>
  );
};

export default Roadmap;
