import React from 'react';
import { motion } from 'framer-motion';
import './css/Roadmaps.css';

const roadmapItems = [
  {
    title: 'Started Learning',
    description: 'Started my journey in the tech world by learning basic HTML, CSS, and JavaScript.'
  },
  {
    title: 'First Project',
    description: 'Developed my first web application, a simple to-do list app.'
  },
  {
    title: 'Professional Experience',
    description: 'Started working as a junior developer at a tech company, gaining valuable experience.'
  },
  {
    title: 'Current Position',
    description: 'Currently working as a full-stack developer, focusing on modern web technologies and AI integration.'
  }
];

const arrowVariants = {
  animate: {
    rotate: [0, 45, 0],
    transition: {
      repeat: Infinity,
      duration: 1,
      ease: "easeInOut"
    }
  }
};

const Roadmap = () => {
  return (
    <div className="roadmap">
      {roadmapItems.map((item, index) => (
        <motion.div
          key={index}
          className="roadmap-item"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: index * 0.2 }}
        >
          <div className="roadmap-content">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
          {index < roadmapItems.length - 1 && (
            <motion.div
              className="arrow"
              variants={arrowVariants}
              animate="animate"
            />
          )}
        </motion.div>
      ))}
    </div>
  );
};

export default Roadmap;
