import React, { useEffect, useRef, useState } from 'react';
import './css/Code.css';

const CodingWork = () => {
  const wrapperRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const wrapper = wrapperRef.current;
    const tiles = wrapper.querySelectorAll('.coding-work-tile');
    const numTiles = tiles.length;
    const width = 300; // Width of each tile plus any margin

    tiles.forEach((tile, index) => {
      const x = width * index;
      tile.style.transform = `translateX(${x}px)`;
    });

    let xOffset = 0;
    const slide = () => {
      if (!isHovered) {
        xOffset -= 1; // Adjust speed as needed
        wrapper.style.transform = `translateX(${xOffset}px)`;
      }
      requestAnimationFrame(slide);
    };

    slide();
  }, [isHovered]);

  const handleTileClick = (e) => {
    e.stopPropagation(); // Prevent event from bubbling up
    window.location.href = '/coding-work'; // Redirect to the Coding Work page
  };

  const handleCarouselClick = () => {
    window.location.href = '/coding-work'; // Redirect to the Coding Work page
  };

  return (
    <div
      className={`coding-work-carousel ${isHovered ? 'hovered' : ''}`}
      onClick={handleCarouselClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="coding-work-wrapper" ref={wrapperRef}>
        <div className="coding-work-tile" onClick={handleTileClick}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 1" />
          <div className="coding-work-content">
            <h2>Project Title 1</h2>
            <p>Description about Project 1 goes here...</p>
          </div>
        </div>
        <div className="coding-work-tile" onClick={handleTileClick}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 2" />
          <div className="coding-work-content">
            <h2>Project Title 2</h2>
            <p>Description about Project 2 goes here...</p>
          </div>
        </div>
        <div className="coding-work-tile" onClick={handleTileClick}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 3" />
          <div className="coding-work-content">
            <h2>Project Title 3</h2>
            <p>Description about Project 3 goes here...</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CodingWork;
