import React, { useEffect, useRef } from 'react';
import './css/Code.css';

const CodingWork = () => {
  const wrapperRef = useRef(null);

  useEffect(() => {
    const wrapper = wrapperRef.current;
    const tiles = wrapper.querySelectorAll('.coding-work-tile');
    const numTiles = tiles.length;
    const angle = 360 / numTiles;
    const radius = 200; // Adjust radius as needed

    tiles.forEach((tile, index) => {
      const theta = angle * index;
      const x = radius * Math.cos(theta * (Math.PI / 180));
      const z = radius * Math.sin(theta * (Math.PI / 180));
      tile.style.transform = `translate3d(${x}px, 0, ${z}px)`;
      tile.style.transformOrigin = `0 0 ${radius}px`;
    });

    let angleOffset = 0;
    const rotate = () => {
      angleOffset += 1;
      wrapper.style.transform = `rotateY(${angleOffset}deg)`;
      requestAnimationFrame(rotate);
    };

    rotate();

    // Pause rotation on hover
    const stopRotation = () => {
      wrapper.style.animationPlayState = 'paused';
    };

    // Resume rotation on mouse leave
    const resumeRotation = () => {
      wrapper.style.animationPlayState = 'running';
    };

    wrapper.addEventListener('mouseover', stopRotation);
    wrapper.addEventListener('mouseout', resumeRotation);

    // Cleanup event listeners on component unmount
    return () => {
      wrapper.removeEventListener('mouseover', stopRotation);
      wrapper.removeEventListener('mouseout', resumeRotation);
    };
  }, []);

  // Redirect on card click
  const handleCardClick = (url) => {
    window.location.href = url; // Navigate to the specified URL
  };

  return (
    <div className="coding-work-carousel">
      <div className="coding-work-wrapper" ref={wrapperRef}>
        <div className="coding-work-tile" onClick={() => handleCardClick('https://example.com/project1')}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 1" />
          <div className="coding-work-content">
            <h2>Project Title 1</h2>
            <p>Description about Project 1 goes here...</p>
          </div>
        </div>
        <div className="coding-work-tile" onClick={() => handleCardClick('https://example.com/project2')}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 2" />
          <div className="coding-work-content">
            <h2>Project Title 2</h2>
            <p>Description about Project 2 goes here...</p>
          </div>
        </div>
        <div className="coding-work-tile" onClick={() => handleCardClick('https://example.com/project3')}>
          <img src="https://w7.pngwing.com/pngs/914/758/png-transparent-github-social-media-computer-icons-logo-android-github-logo-computer-wallpaper-banner-thumbnail.png" alt="Project 3" />
          <div className="coding-work-content">
            <h2>Project Title 3</h2>
            <p>Description about Project 3 goes here...</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CodingWork;
