import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import BoyWithSunglasses from './Image/3d-render-boy-with-orange-tshirt-sunglasses.jpg';
import Roadmap from './Roadmap';
import './Home.css';
import { personalDetails } from "../Details";

const Home = () => {
  const { name, tagline, img } = personalDetails;
  const h11 = useRef();
  const h12 = useRef();
  const h13 = useRef();
  const myimageref = useRef();

  useEffect(() => {
    const tl = gsap.timeline();
    tl.from(
      h11.current,
      {
        x: "-100%",
        delay: 0.8,
        opacity: 0,
        duration: 2,
        ease: "Power3.easeOut",
      },
      "<"
    )
      .from(
        h12.current,
        {
          x: "-100%",
          delay: 0.5,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      )
      .from(
        h13.current,
        {
          x: "-100%",
          delay: 0.1,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      )
      .from(
        myimageref.current,
        {
          x: "200%",
          delay: 0.5,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      );
  }, []);

  return (
    <div className="home-container">
      <div className="about-me">
        <h2 ref={h11}>About Me</h2>
        <p ref={h12}>Your about me content goes here...</p>
      </div>
      <Roadmap />
      <div className="grid-container">
        <div className="grid-item animated-image" ref={myimageref}>
          <img src={BoyWithSunglasses} alt="3D Render Boy with Orange T-Shirt and Sunglasses" />
        </div>
        <div className="grid-item coding-work">
          <a href="/coding-work">
            <div className="tile">
              <h2 ref={h13}>Coding Work</h2>
              <p>Details about your coding projects go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
        <div className="grid-item photographs">
          <a href="/photographs">
            <div className="tile">
              <h2>Photographs</h2>
              <p>Details about your photographs go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
      </div>
    </div>
  );
};

export default Home;
