import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import BoyWithSunglasses from './Image/3d-render-boy-with-orange-tshirt-sunglasses.jpg';
import Roadmap from './Roadmap';
import './Home.css';

const Home = () => {
  const aboutMeRef = useRef(null);
  const gridItemRefs = useRef([]);

  useEffect(() => {
    // Animation for the "About Me" section
    gsap.from(aboutMeRef.current, {
      opacity: 0,
      x: -50,
      duration: 1,
      delay: 0.5,
    });

    // Animation for grid items
    gsap.from(gridItemRefs.current, {
      opacity: 0,
      y: 50,
      duration: 1,
      delay: 1.5, // Adjust delay to ensure "About Me" animation finishes first
      stagger: 0.2, // Stagger the animation of each grid item
    });
  }, []);

  return (
    <div className="home-container">
      <div className="about-me" ref={aboutMeRef}>
        <h2>About Me</h2>
        <p>Your about me content goes here...</p>
      </div>
      <Roadmap />
      <div className="grid-container">
        <div className="grid-item animated-image" ref={el => (gridItemRefs.current[0] = el)}>
          <img src={BoyWithSunglasses} alt="3D Render Boy with Orange T-Shirt and Sunglasses" />
        </div>
        <div className="grid-item coding-work" ref={el => (gridItemRefs.current[1] = el)}>
          <a href="/coding-work">
            <div className="tile">
              <h2>Coding Work</h2>
              <p>Details about your coding projects go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
        <div className="grid-item photographs" ref={el => (gridItemRefs.current[2] = el)}>
          <a href="/photographs">
            <div className="tile">
              <h2>Photographs</h2>
              <p>Details about your photographs go here...</p>
              <p className="more">More</p>
            </div>
          </a>
        </div>
      </div>
    </div>
  );
};

export default Home;
