import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import './css/CodingWork.css';

const CodingWork = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/projects')
      .then(response => setProjects(response.data))
      .catch(error => console.error('Error fetching projects:', error));
  }, []);

  return (
    <div className="coding-work-page">
      <h2>Coding Work</h2>
      <div className="project-list">
        {projects.map(project => (
          <motion.div 
            key={project._id} 
            className="project"
            whileHover={{ scale: 1.05, rotate: 5 }} 
            transition={{ type: 'spring', stiffness: 300 }}
          >
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            {project.githubLink && <p className="github-link"><a href={project.githubLink} target="_blank" rel="noopener noreferrer">GitHub Repo</a></p>}
            {/* {project.backgroundImage && <div className="background-image"><img src={project.backgroundImage} alt="Background" /></div>} */}
          </motion.div>
        ))}
      </div>
      <div className="footer">
        <p>Footer Content Goes Here</p>
      </div>
    </div>
  );
};

export default CodingWork;
