// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import CodingWork from './components/CodingWork';
import Photographs from './components/Photographs';
import AdminPanel from './components/AdminPanel';
import SignIn from './components/SignIn';
import 'bootstrap/dist/css/bootstrap.min.css';
import './components/Home.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleSignIn = () => {
    setIsAuthenticated(true);
  };

  const handleSignOut = () => {
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} onSignOut={handleSignOut} />
        <div className="home-container">
          <div className="float-container">
            <div className="float-child about-me" style={{backgroundImage: `url(${process.env.PUBLIC_URL}/path/to/animated-background-image.gif)`}}>
              <h2>About Me</h2>
              <p>Your about me content goes here...</p>
            </div>
            <div className="float-child animated-image">
              <img src={`${process.env.PUBLIC_URL}/src/components/Image/3d-render-boy-with-orange-tshirt-sunglasses.jpg`} alt="Inside Image" />
            </div>
          </div>
        </div>

        <Routes>
          <Route path="/coding-work" element={<CodingWork />} />
          <Route path="/photographs" element={<Photographs />} />
          <Route path="/sign-in" element={<SignIn onSignIn={handleSignIn} />} />
          <Route
            path="/admin"
            element={isAuthenticated ? <AdminPanel /> : <Navigate to="/sign-in" />}
          />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
