import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { personalDetails } from "../Details";

function Home() {
  const { name, tagline, img } = personalDetails;
  const h11 = useRef();
  const h12 = useRef();
  const h13 = useRef();
  const myimageref = useRef();
  useEffect(() => {
    const tl = gsap.timeline();
    tl.from(
      h11.current,
      {
        x: "-100%",
        delay: 0.8,
        opacity: 0,
        duration: 2,
        ease: "Power3.easeOut",
      },
      "<"
    )
      .from(
        h12.current,
        {
          x: "-100%",
          delay: 0.5,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      )
      .from(
        h13.current,
        {
          x: "-100%",
          delay: 0.1,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      )
      .from(
        myimageref.current,
        {
          x: "200%",
          delay: 0.5,
          opacity: 0,
          duration: 2,
          ease: "Power3.easeOut",
        },
        "<"
      );
  }, []);

  return (
    <main className="home-container flex justify-between items-center">
      <div className="home-content">
        <h1 ref={h11} className="home-heading">
          Hi, ðŸ‘‹
          <br />
          My Name is
          <br />
          <span ref={h12} className="gradient-text">
            {name}
          </span>
          <br />
          <span ref={h13} className="home-tagline">
            {tagline}
          </span>
        </h1>
      </div>
      <div className="home-image">
        <img ref={myimageref} src={img} alt="Pavan MG" />
      </div>
    </main>
  );
}

export default Home;
