import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './css/Photograph.css'; // Ensure this path is correct

const Photographs = () => {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/photos')
      .then(response => setPhotos(response.data))
      .catch(error => console.error('Error fetching photos:', error));

    // Carousel initialization script
    setTimeout(() => {
      const radius = 240;
      const autoRotate = true;
      const rotateSpeed = -60;
      const imgWidth = 120;
      const imgHeight = 170;

      const bgMusicURL = 'https://user-images.githubusercontent.com/151072490/283747943-7b08424b-8647-4bdc-996c-965063dbb5e3.mp4';
      const bgMusicControls = true;

      const odrag = document.getElementById('drag-container');
      const ospin = document.getElementById('spin-container');
      const aImg = ospin.getElementsByTagName('img');
      const aEle = [...aImg];

      ospin.style.width = imgWidth + "px";
      ospin.style.height = imgHeight + "px";

      const ground = document.getElementById('ground');
      ground.style.width = radius * 3 + "px";
      ground.style.height = radius * 3 + "px";

      function init(delayTime) {
        for (let i = 0; i < aEle.length; i++) {
          aEle[i].style.transform = "rotateY(" + (i * (360 / aEle.length)) + "deg) translateZ(" + radius + "px)";
          aEle[i].style.transition = "transform 1s";
          aEle[i].style.transitionDelay = delayTime || (aEle.length - i) / 4 + "s";
        }
      }

      function applyTransform(obj) {
        obj.style.transform = "rotateX(" + (-tY) + "deg) rotateY(" + (tX) + "deg)";
      }

      function playSpin(yes) {
        ospin.style.animationPlayState = (yes ? 'running' : 'paused');
      }

      let sX, sY, nX, nY, desX = 0,
          desY = 0,
          tX = 0,
          tY = 10;

      if (autoRotate) {
        const animationName = (rotateSpeed > 0 ? 'spin' : 'spinRevert');
        ospin.style.animation = `${animationName} ${Math.abs(rotateSpeed)}s infinite linear`;
      }

      if (bgMusicURL) {
        document.getElementById('music-container').innerHTML += `
          <audio src="${bgMusicURL}" ${bgMusicControls ? 'controls' : ''} autoplay loop>    
          <p>If you are reading this, it is because your browser does not support the audio element.</p>
          </audio>
        `;
      }

      document.onpointerdown = function (e) {
        clearInterval(odrag.timer);
        e = e || window.event;
        sX = e.clientX;
        sY = e.clientY;

        this.onpointermove = function (e) {
          e = e || window.event;
          nX = e.clientX;
          nY = e.clientY;
          desX = nX - sX;
          desY = nY - sY;
          tX += desX * 0.1;
          tY += desY * 0.1;
          applyTransform(odrag);
          sX = nX;
          sY = nY;
        };

        this.onpointerup = function () {
          odrag.timer = setInterval(function () {
            desX *= 0.95;
            desY *= 0.95;
            tX += desX * 0.1;
            tY += desY * 0.1;
            applyTransform(odrag);
            playSpin(false);
            if (Math.abs(desX) < 0.5 && Math.abs(desY) < 0.5) {
              clearInterval(odrag.timer);
              playSpin(true);
            }
          }, 17);
          this.onpointermove = this.onpointerup = null;
        };

        return false;
      };

      document.onmousewheel = function (e) {
        e = e || window.event;
        const d = e.wheelDelta / 20 || -e.detail;
        radius += d;
        init(1);
      };

      init();
    }, 1000);
  }, []);

  return (
    <div className="page-container">
      <div className="page-content">
        <h2>Photographs</h2>
        <div id="drag-container">
          <div id="spin-container">
            {photos.map(photo => (
              <img key={photo._id} src={photo.url} alt={photo.title} />
            ))}
            <p>Your custom text here</p>
          </div>
          <div id="ground"></div>
        </div>
        <div id="music-container"></div>
      </div>
    </div>
  );
};

export default Photographs;
