// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import CodingWork from './components/CodingWork';
import Photographs from './components/Photographs';
import SignIn from './components/SignIn';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css'; // Import CSS for homepage styling

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleSignIn = () => {
    setIsAuthenticated(true);
  };

  const handleSignOut = () => {
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} onSignOut={handleSignOut} />
        <div className="homepage-container">
          <div className="about-me">
            <h2>About Me</h2>
            <p>Add your about me content here.</p>
          </div>
          <div className="animated-image">
            {/* Add your animated image related to coding here */}
          </div>
          <div className="photographs-and-coding-work">
            <Photographs />
            <CodingWork />
          </div>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
