import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './css/Navbar.css';
import resumePdf from '../assets/JayeshResume.pdf';

const Navbar = ({ isAuthenticated, onSignOut }) => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.nav 
      className={`navbar ${scrolled ? 'scrolled' : ''}`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: 'spring', stiffness: 100 }}
    >
      <motion.h1 
        className="logo"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <Link to="./">My Portfolio</Link>
      </motion.h1>
      <motion.ul 
        className="nav-links"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.5 }}
      >
        <motion.li whileHover={{ scale: 1.1 }}>
          <Link to="/coding-work">Coding Work</Link>
        </motion.li>
        <motion.li whileHover={{ scale: 1.1 }}>
          <Link to="/photographs">Photographs</Link>
        </motion.li>
        {isAuthenticated ? (
          <>
            <motion.li whileHover={{ scale: 1.1 }}>
              <Link to="/admin">Admin Panel</Link>
            </motion.li>
            <motion.li>
              <button onClick={onSignOut}>Sign Out</button>
            </motion.li>
          </>
        ) : (
          <motion.li whileHover={{ scale: 1.1 }}>
            <Link to="/sign-in">Sign In</Link>
          </motion.li>
        )}
        <motion.li whileHover={{ scale: 1.1 }}>
          <a href={resumePdf} download="JayeshResume.pdf">
            <motion.button 
              className="resume-download"
              whileHover={{ scale: 1.1, backgroundColor: "#ff8a7b" }}
              whileTap={{ scale: 0.95 }}
            >
              Download Resume
            </motion.button>
          </a>
        </motion.li>
      </motion.ul>
    </motion.nav>
  );
};

export default Navbar;
